package com.eggman.mortar.androidimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.unit.Dpimport com.eggman.tokens.model.MortarTheme/** * A compose representation of a [MortarTheme] that contains all mortar colors, typography, and components * for a given theme, with their respective compose values. * * These values are in compose specific format (e.g., [Color], [TextStyle]). */data class MortarComposeTheme(    val colors: MortarComposeColors,    val typography: MortarComposeTypography,    val components: MortarComposeComponents)data class MortarComposeColors(    val primary: Color,    val onPrimary: Color,    val primaryContainer: Color,    val onPrimaryContainer: Color,    val secondary: Color,    val onSecondary: Color,    val secondaryContainer: Color,    val onSecondaryContainer: Color,    val background: Color,    val onBackground: Color,    val surface: Color,    val onSurface: Color,    val error: Color,    val onError: Color,    val errorContainer: Color,    val onErrorContainer: Color,    val disabledPrimary: Color,    val disabledOnPrimary: Color)data class MortarComposeTypography(    val bodySmall: TextStyle, val bodyMedium: TextStyle, val bodyLarge: TextStyle)data class MortarComposeComponents(    val buttonPrimary: MortarComposeButtonStyle, val buttonSecondary: MortarComposeButtonStyle)data class MortarComposeButtonStyle(    val containerColor: Color,    val contentColor: Color,    val disabledContentColor: Color,    val disabledContainerColor: Color,    val radius: Dp,    val elevation: Dp,    val textStyle: TextStyle)